<template>
	<div class="pixel-art-wrapper" :style="cssVars">
		<div class="pixelart-to-css" />
	</div>
</template>

<script>
	export default {
		name: 'GradedLogo',
		props: {
			size: {
				type: Number,
				default: 1
			},
			color1: {
				type: String,
				default: '#40C057'
			},
			color2: {
				type: String,
				default: '#FFFFFF'
			}
